package main

import "testing"

func Test_longestPalindrome(t *testing.T) {
	type args struct {
		s string
	}
	tests := []struct {
		name string
		args args
		want string
	}{
		{
			args: args{s: "babad"},
			// want: "bab",
			want: "bab",
		},
		{
			args: args{s: "cbbd"},
			want: "bb",
		},
		{
			args: args{s: "ac"},
			want: "a",
		},
		{
			args: args{s: "a"},
			want: "a",
		},
		{
			args: args{s: "aacabdkacaa"},
			want: "aca",
		},
		{
			args: args{s: "bb"},
			want: "bb",
		},
		{
			args: args{s: "abcdasdfghjkldcba"},
			want: "a",
		},
		{
			args: args{s: ""},
			want: "",
		},
	}
	for _, tt := range tests {
		if got := longestPalindrome(tt.args.s); got != tt.want {
			t.Errorf("%v. longestPalindrome() = %v, want %v", tt.args.s, got, tt.want)
		}
	}
}
